INSERT INTO `wp_comments` VALUES (53, 12732, 'COMO MONTAR UM PLANO DE NEGÓCIOS EM 6 PASSOS. &#8211; fabrica de empreender', '', 'https://fabricadeempreeder.com/como-montar-um-plano-de-negocios-em-6-passos/', '162.241.203.157', '2019-06-12 21:15:41', '2019-06-13 00:15:41', '[&#8230;] é possível mostrar o ritmo de crescimento esperado e a quantidade de investimentos que serão necessários ao longo desse período de [&#8230;]', 0, '0', '', 'pingback', 0, 0);
INSERT INTO `wp_comments` VALUES (54, 12988, 'Linhas de crédito incentivam crescimento do setor de franquias &#8211; News Navigator', '', 'http://nav.news/linhas-de-credito-incentivam-crescimento-do-setor-de-franquias/', '108.179.193.135', '2018-06-26 15:26:47', '2018-06-26 18:26:47', '[&#8230;] Everton Sabú Fundador da Jumper!, tem ampla experiência no mercado de franchising, e dá as dicas para quem deseja investir no mercado de franquias. “Apesar das excelentes taxas oferecidas hoje, antes de indicar uma instituição financeira, nós avaliamos todas as possibilidades. Buscar o valor necessário com um amigo ou membro da família é uma alternativa que permite melhor negociação, juros mais baixos e garantias mais flexíveis. Nessa negociação, pode surgir, inclusive, uma sociedade, em que um sócio é o investidor (e oferece o capital) e o outro é o operador (e será responsável pelo dia a dia da operação). Muitos franqueadores, aliás, iniciam assim os seus negócios.”, aponta Everton. [&#8230;]', 0, '1', '', 'pingback', 0, 0);
INSERT INTO `wp_comments` VALUES (55, 12988, 'Linhas de crédito incentivam crescimento do setor de franquias', '', 'https://sejaumfranqueado.com.br/linhas-de-credito-incentivam-crescimento-do-setor-de-franquias/', '191.252.51.242', '2018-07-08 21:00:37', '2018-07-09 00:00:37', '[&#8230;] Everton Sabú Fundador da Jumper!, tem ampla experiência no mercado de franchising, e dá as dicas para quem deseja investir no mercado de franquias. “Apesar das excelentes taxas oferecidas hoje, antes de indicar uma instituição financeira, nós avaliamos todas as possibilidades. Buscar o valor necessário com um amigo ou membro da família é uma alternativa que permite melhor negociação, juros mais baixos e garantias mais flexíveis. Nessa negociação, pode surgir, inclusive, uma sociedade, em que um sócio é o investidor (e oferece o capital) e o outro é o operador (e será responsável pelo dia a dia da operação). Muitos franqueadores, aliás, iniciam assim os seus negócios.”, aponta Everton. [&#8230;]', 0, '0', '', 'pingback', 0, 0);
