INSERT INTO `wp_posts` VALUES (14264, 2, '2019-06-05 15:46:51', '2019-06-05 18:46:51', '[embed]https://youtu.be/7ApjSL0SahU[/embed]\n\nCaptação de Vídeo: <a class=\"profileLink\" href=\"https://www.facebook.com/tendafilms/?__tn__=K-R&amp;eid=ARD63wHD4jOXHyLF-PTREVMXXl81wCw19WBY-YXbW7VoyAGXm-4j9-f8PAVLsS8d5dS--a1nWxw3xxm6&amp;fref=mentions&amp;__xts__%5B0%5D=68.ARDOiCO4YbgDH-8E-5rILarKzr554Zt90_udpa-1NZnz1bHFq2DSobk_oPkMa0AGO714IlybBrP-JiyQWP6lX44T_Uv9mlAjNCIMiJhwJhEHkJr3aA8ZExc97f8A6z4B_DUgMkeeT9ta_lpjTbc6FlOzWYg5kemx0PHLHTFwXTln7QdtCxQV9eJzNg2IzIHCJV1khepk2Jm1a77kXdrtaWh4x3EPFZWH7l8zLPUsN0L3DiwCyoIB418FUX_NuNLIA2shgwcHT1C0PaNmAPvK8p34gkxqpI6N21du-kPBgnnCIXG_goE5mpDDBW36P0yEaj1gfv8wAF_qHCZ_GOLdv1T22ZMP\" data-hovercard=\"/ajax/hovercard/page.php?id=680360985431028&amp;extragetparams=%7B%22__tn__%22%3A%22%2CdK-R-R%22%2C%22eid%22%3A%22ARD63wHD4jOXHyLF-PTREVMXXl81wCw19WBY-YXbW7VoyAGXm-4j9-f8PAVLsS8d5dS--a1nWxw3xxm6%22%2C%22fref%22%3A%22mentions%22%7D\" data-hovercard-prefer-more-content-show=\"1\">Leonardo Silva Cinematographer</a>\nEdição: <a class=\"profileLink\" title=\"Sinara Bezerra\" href=\"https://www.facebook.com/sinara.hellen?__tn__=K-R&amp;eid=ARBfYnHDaPi7upiQRl0T0i_f-DH8LbT8hYqavg1Ug8nakH0ZGHrMYQNLkRXMNI69stcY8bhSPFRYYsOD&amp;fref=mentions&amp;__xts__%5B0%5D=68.ARDOiCO4YbgDH-8E-5rILarKzr554Zt90_udpa-1NZnz1bHFq2DSobk_oPkMa0AGO714IlybBrP-JiyQWP6lX44T_Uv9mlAjNCIMiJhwJhEHkJr3aA8ZExc97f8A6z4B_DUgMkeeT9ta_lpjTbc6FlOzWYg5kemx0PHLHTFwXTln7QdtCxQV9eJzNg2IzIHCJV1khepk2Jm1a77kXdrtaWh4x3EPFZWH7l8zLPUsN0L3DiwCyoIB418FUX_NuNLIA2shgwcHT1C0PaNmAPvK8p34gkxqpI6N21du-kPBgnnCIXG_goE5mpDDBW36P0yEaj1gfv8wAF_qHCZ_GOLdv1T22ZMP\" data-hovercard=\"/ajax/hovercard/user.php?id=100001383065075&amp;extragetparams=%7B%22__tn__%22%3A%22%2CdK-R-R%22%2C%22eid%22%3A%22ARBfYnHDaPi7upiQRl0T0i_f-DH8LbT8hYqavg1Ug8nakH0ZGHrMYQNLkRXMNI69stcY8bhSPFRYYsOD%22%2C%22fref%22%3A%22mentions%22%7D\" data-hovercard-prefer-more-content-show=\"1\">Sinara Bezerra</a>\nEspaço: <a class=\"profileLink\" href=\"htt